// import { border } from '@mui/system';
import { useSelector } from 'react-redux';

import { useRef } from 'react';

import { useHistory } from 'react-router-dom';

import classes from './ProfileForm.module.css';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ProfileForm = () => {
  const email = useSelector(state => state.auth.email);
  const id = useSelector(state => state.auth.id);
  const newPasswordRef = useRef();
  const history = useHistory();

  const editPasswrdHanler = (event) => {
    event.preventDefault();
    const newPassword = newPasswordRef.current.value;
    fetch('https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyBSrVcbFFY5pAuN1s0ouA6h_Tx3J3UnvgQ', {
      method: 'POST',
      body: JSON.stringify({
        idToken: id,
        password: newPassword,
        returnSecureToken: false,

      }),
      headers: {
        'Content-Type': 'application/json',
      }
    }).then(res => {
      console.log(res);
      history.push('/');

    })
    console.log(newPassword);
  };

  return (
    <form className={classes.form} onSubmit={editPasswrdHanler}>
      <div className={classes.control}>
        <label htmlFor='new-password'>New Password</label>
        <input type='password' id='new-password' minLength={6} ref={newPasswordRef} />
      </div>
      <div className={classes.action}>
        <button>
          Change Password
          <ToastContainer
            position="top-center"
            autoClose={1500}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick={false}
            rtl
            pauseOnFocusLoss
            draggable
            pauseOnHover
            theme="colored"
          />
        </button>
        <p>{email}</p>
        {/* <p>{id}</p> */}
      </div>
    </form>
  );
}

export default ProfileForm;
