import { useState, useRef } from 'react';

import classes from './AuthForm.module.css';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);
  const entredEmil = useRef();
  const entredPassword = useRef();

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const authHandler = (event) => {
    event.preventDefault();
    if (isLogin) {

    } else {
      fetch('https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyBSrVcbFFY5pAuN1s0ouA6h_Tx3J3UnvgQ',
        {
          method: 'POST',
          bady:JSON.stringify({
            email:entredEmil,
            password:entredPassword,
            returnSecureToken:true,
          }),
          headers:{
            'Content-Type': 'application/json',
          }
        },
      ).then(res =>{
        if(res.ok) {
          //do
        }else{
          res.json.then(data =>{
            console.log(data);
          });
        }
      })

    }
  };

  return (
    <section className={classes.auth}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={authHandler}>
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input type='email' id='email' required ref={entredEmil} />
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input type='password' id='password' required ref={entredPassword} />
        </div>
        <div className={classes.actions}>
          <button>{isLogin ? 'Login' : 'Create Account'}</button>
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default AuthForm;
