import { createSlice } from "@reduxjs/toolkit";

// const initialAuthState = { isAuth: false, email: null, id: null };
const initialAuthState = {
    isAuth: localStorage.getItem('steat.isAuth'),
    email:localStorage.getItem('steat.email'),
    id: localStorage.getItem('steat.id'),
};
// const initialAuthState = localStorage.getItem('steat');
const authSlice = createSlice({
    name: 'auth',
    initialState: initialAuthState,
    reducers: {
        login(steat, action) {
            steat.isAuth = true;
            steat.email = action.payload.email;
            steat.id = action.payload.id;
            localStorage.setItem({
                'steat.isAuth': steat.isAuth,
                'steat.email': steat.email,
                'steat.id': steat.id,
            });
            
        },
        logout(steat) {
            steat.isAuth = false;
            steat.email = null;
            steat.id = null;
            localStorage.clear();
        },

    }
})
export const authAction = authSlice.actions;
export default authSlice.reducer;


